import axios from 'axios';
import React, { Fragment } from 'react';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';

import { toast } from 'react-toastify';

import { onMessageListener } from './firebaseInit';

import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import './App.scss';
import { requestFirebaseNotificationPermission } from './firebaseInit';

const divStyle = {
  margin: '40px',
  border: '5px solid pink',
  height: '40px',
};

axios.defaults.baseURL = 'http://localhost:3001/v1';

const App = () => {
  const [ token, setToken ] = React.useState('');

  const getFirebaseToken = async() => {
    const requestFNP = await requestFirebaseNotificationPermission();
    setToken(requestFNP);
  }

  onMessageListener()
    .then((payload) => {
      console.log(payload.data);
      const { title, body } = payload.data;
      toast.info(`${title}; ${body}`);
    })
    .catch((err) => {
      toast.error(JSON.stringify(err));
    });

  return (
    <Fragment>
      <ToastContainer
        position="top-center"
        autoClose={6000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <Navbar bg="danger" variant="dark">
        <Navbar.Brand href="#home">
          Firebase notifictations with React and NestJs
        </Navbar.Brand>
      </Navbar>

      <Container className="center-column">
        <Row>
          <Button variant="success" onClick={() => getFirebaseToken()}>Get Firebase Token</Button>{' '}
        </Row>
          <Col style = {divStyle}>
            <div>
            { token }
            </div>
          </Col>  
        {/* <Row>
          <Col>
            <Messaging />
          </Col>
        </Row> */}
      </Container>
    </Fragment>
  );
};

export default App;
