import React, { Fragment } from 'react';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';

import { toast } from 'react-toastify';

import { messaging, onMessageListener } from './firebaseInit';

import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import './App.scss';
import { requestFirebaseNotificationPermission } from './firebaseInit';

const divStyle = {
  margin: '40px',
  border: '1px solid #28a745',
  width: '85%',
  wordBreak: 'break-all', 
  minHeight: '80px'
};

const App = () => {
  const [ token, setToken ] = React.useState('');

  const getFirebaseToken = async() => {
    const requestFNP = await requestFirebaseNotificationPermission();
    setToken(requestFNP);
  }
  const payload = messaging.onMessage(payload);

  console.log(payload);
  // onMessageListener()
  //   .then((payload) => {
  //     const { title, body } = payload.data;
  //     toast.info(`${title}; ${body}`);
  //   })
  //   .catch((err) => {
  //     toast.error(JSON.stringify(err));
  //   });

  return (
    <Fragment>
      <ToastContainer
        position="top-center"
        autoClose={6000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <Navbar bg="success" variant="dark">
        <Navbar.Brand href="#home">
          Firebase notifictations with React and NestJs
        </Navbar.Brand>
      </Navbar>

      <Container className="center-column">
        <Row>
          <Button variant="success" onClick={() => getFirebaseToken()}>Get Firebase Token</Button>{' '}
        </Row>
          <Col style = {divStyle}>
            { token }
          </Col>  
      </Container>
    </Fragment>
  );
};

export default App;
